import { Howler, Howl } from "howler";
import cityTraffic from "../assets/city-traffic.ogg";

const trafficData = {
  src: [cityTraffic],
  volume: 1,
  loop: true,
  stereo: 0.5
};

let trafficHowl = new Howl(trafficData);

export const playSound = () => {
  trafficHowl.play();
};

export const pauseSound = () => {
  console.log(trafficHowl);
  trafficHowl.pause();
};

export const volSound = vol => {
  trafficHowl.volume(vol);
};

export const panSound = pan => {
  console.log(pan);
  trafficHowl.stereo(pan);
};
