import React, { Component } from "react";
import "./App.css";
import * as sounds from "./utils/sounds";

class App extends Component {
  state = {
    playing: false,
    volume: 0.7,
    pan: 0.5
  };

  handleVolume = event => {
    const { value } = event.target;
    const { volume } = this.state;
    this.setState({ volume: value });
    sounds.volSound(volume);
  };

  handlePan = event => {
    const { value } = event.target;
    const { pan } = this.state;
    this.setState({ pan: value });
    sounds.panSound(pan);
  };

  render() {
    const { playSound } = sounds;
    const { volume, pan } = this.state;
    return (
      <div className="App">
        <button onClick={playSound}>Play</button>
        <label>
          Volume
          <input
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={this.handleVolume}
          ></input>
        </label>
        <label>
          Pan
          <input
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={pan}
            onChange={this.handlePan}
          ></input>
        </label>
      </div>
    );
  }
}

export default App;
