import { Howler, Howl } from "howler";

const seagull = new Howl({
  src: ["../assets/seagull1.ogg"],
  volume: 0.7,
  loop: false,
  html5: false
});

const createHowl = ({ src, volume, loop }) => {};

export const playSound = () => {
  console.log("play");
  seagull.play();
};
