import { Howler, Howl } from "howler";

const seagullData = {
  src: ["../assets/seagull1.ogg"],
  volume: 0.7,
  loop: false
};

const createHowl = ({ src, volume, loop }) => {
  return new Howl({ src: src, volume: volume, loop: loop });
};

export const playSound = () => {
  const seagullHowl = createHowl({ seagullData });
  seagullHowl.play();
};
