import { Howler, Howl } from "howler";

const seagullData = {
  src: ["../assets/seagull1.ogg"],
  volume: 0.7,
  loop: false
};

const createHowl = data => {
  return new Howl(data);
};

export const playSound = () => {
  const seagullHowl = createHowl(seagullData);
  seagullHowl.play();
};
