import React, { Component } from "react";
import "./App.css";
import * as sounds from "./utils/sounds";

class App extends Component {
  state = {
    volume: 0.7,
    pan: 0.5
  };

  handleVolume = event => {
    const { value } = event.target;
    this.setState({ volume: value });
  };

  handlePan = event => {
    const { value } = event.target;
    this.setState({ pan: value });
  };

  render() {
    const { playSound } = sounds;
    const { volume, pan } = this.state;
    return (
      <div className="App">
        <button onClick={playSound}>Play</button>
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChane={this.handleVolume}
        >
          Volume
        </input>
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={pan}
          onChange={this.handlePan}
        >
          Pan
        </input>
      </div>
    );
  }
}

export default App;
