import { Howler, Howl } from "howler";
import cityTraffic from "../assets/city-traffic.ogg";

const trafficData = {
  src: [cityTraffic],
  volume: 1,
  loop: true,
  stereo: 0.5
};

export const playSound = () => {
  const trafficHowl = new Howl(trafficData);
  trafficHowl.play();
};
