import { Howler, Howl } from "howler";
import seagull1 from "../assets/seagull1.ogg";

const seagullData = {
  src: [seagull1],
  volume: 1,
  loop: false
};

const createHowl = data => {
  return new Howl(data);
};

const seagullHowl = new Howl(seagullData);

export const playSound = () => {
  const seagullHowl = createHowl(seagullData);
  seagullHowl.play();
};
